	private static String getSession(int question) {
		if (question <= 30) {
			return "1";
		} else if (question >= 31 && question <= 60) {
			return "2";
		} else if (question >= 61 && question <= 90) {
			return "3";
		} else if (question >= 91 && question <= 120) {
			return "4";
		} else if (question >= 121) {
			return "5";
		} else {
			return "";
		}
	}


